sp_widget.client_script:
function ($scope, $sce, $location, spUtil, $rootScope) {
  var c = this;


	c.setField = function (value) {
		$scope.page.g_form.setValue("service", value);
	};

	c.getField = function () {
		var checked = '';

		if ($scope.page.g_form.getValue("service") == 'false'){
			checked = "checked";
		}
		else
			{
				checked = "";
			}

		return 'checked';

	};

	c.changeStyle = function (value) {

	if (value == 'summer'){
		document.getElementById('summer').className = 'style_block';
		document.getElementById('new').className = 'style_unblock';
	}
		else
			{
				document.getElementById('new').className = 'style_block';
		document.getElementById('summer').className = 'style_unblock';
			}


};

}

